import axios from "axios";
import { configHeader } from "../components/functions/configHeader";

var env = require('../env');

var endpoint = env.endpoint;

var listGames = function listGames() {
  return axios.get("".concat(endpoint, "/gamelist"), configHeader());
};

var getGames = function getGames(id) {
  return axios.get("".concat(endpoint, "/gamelist/").concat(id), configHeader());
};

var listGamesFromBrand = function listGamesFromBrand(brand) {
  return axios.get("".concat(endpoint, "/gamelistwithbrand/").concat(brand), configHeader());
};

var createGames = function createGames(data) {
  //console.log('data : ' + data.password);
  return axios.post("".concat(endpoint, "/gamelist"), data, configHeader());
};

var editGames = function editGames(data, id) {
  //console.log('data : ' + data);
  return axios.put("".concat(endpoint, "/gamelist/").concat(id), data, configHeader());
};

var deleteGames = function deleteGames(id) {
  return axios["delete"]("".concat(endpoint, "/gamelist/").concat(id), configHeader());
};

export default {
  listGames: listGames,
  listGamesFromBrand: listGamesFromBrand,
  createGames: createGames,
  editGames: editGames,
  deleteGames: deleteGames,
  getGames: getGames
};
