import Link from 'next/link';
import React, { useState, useEffect, Component } from "react";
import Router from "next/router";
import Cookies from "js-cookie";

import { ServiceAgent, ServiceUserAgent, ServiceBrand, ServiceMember, ServiceGameService, ServiceWallets } from "../../service";
import Dialog from 'react-bootstrap-dialog';

export default class AddWallets extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //auth: { username: "", password: "", role: "3", rolename: "User" },            
            user_name: "",
            user_email: "",
            user_pass: "",
            add_date: "",
            user_level: "3",
            user_status: "", //1=enable , 0=disable
            switch1: true,


            errorMessage: "",
            datas: [],
            role_data: [],
            mydatas: {},
            value: "Master",
            data_service: [],
            data_agent: [],
            data_brand: [],
            data_member: [],
            brand: "",
            mem: "",
            agent: "",
            isDownLine: false,
            game_username: "",
            game_password: "",
            agent_code: "",
