                                return brand.brand_name;
                            })
                            console.log('brand_name : ' + brand_name[0]);
                            my_data_brand.push({ brand_name: brand_name[0], brand_code: data[i].brand_code });
                        }
                        console.log('data_brand : ' + JSON.stringify(my_data_brand));
