    async handleSubmit() {
        try {
            if (this.state.agent_code !== "" && this.state.brand_code !== "" && this.state.game_username !== "" && this.state.game_password !== "") {
                /* let user_status = 0;
                if (this.state.switch1) {
                    user_status = 1;
                } */

                const a = {
                    agent_code: this.state.agent_code, mem_username: this.state.mem,
                    brand_code: this.state.brand_code, game_username: this.state.game_username, game_password: this.state.game_password,
                };
                const res = await ServiceWallets.createWallets(a);
                console.log('res : ' + res.data);
                if (res.data.status === 2000) {
                    console.log('Add Member : '+ this.state.mem +' wallets Success!!!');
                    this.dialog.show({
                        title: 'Slot Admin information',
                        body: 'Add a user agent : ' + a.user_name + ' to system is successfully.',
                        actions: [
                            Dialog.OKAction(() => {
                                Router.push("/agent_users");
                            })
                        ]
                    })
                } else {
                    this.dialog.showAlert(res.data.message);
                    this.setErrorMsg(res.data.msg);
                }
            }
            else {
                this.dialog.showAlert('กรุณาใส่ข้อมูลให้ครบ ก่อนกดปุ่ม Save!');
            }

        } catch (error) {
            this.dialog.showAlert(error);
            console.error("An unexpected error happened occurred:", error);
            this.setErrorMsg(error.message);
        }
        //e.preventDefault();
    }

