import axios from "axios";
import { configHeader } from "../components/functions/configHeader";

var env = require('../env');

var endpoint = env.endpoint;

var listMember = function listMember() {
  return axios.get("".concat(endpoint, "/member"), configHeader());
};

var getBalance = function getBalance(username) {
  return axios.get("".concat(endpoint, "/member/getBalance/").concat(username), configHeader());
};

var getMemberInfo = function getMemberInfo(username) {
  return axios.get("".concat(endpoint, "/member/info/").concat(username), configHeader());
};

var createMember = function createMember(data) {
  //console.log('data : ' + data.password);
  return axios.post("".concat(endpoint, "/member/register"), data, configHeader());
};

var editMember = function editMember(data, username) {
  //console.log('data : ' + data);
  return axios.put("".concat(endpoint, "/member/").concat(username), data, configHeader());
};

var deposit = function deposit(data, username) {
  //console.log('data : ' + data);
  return axios.put("".concat(endpoint, "/member/deposit/").concat(username), data, configHeader());
};

var WithdrawAll = function WithdrawAll(data, username) {
  //console.log('data : ' + data);
  return axios.put("".concat(endpoint, "/member/WithdrawAll/").concat(username), data, configHeader());
};

var deleteMember = function deleteMember(id) {
  return axios["delete"]("".concat(endpoint, "/member/").concat(id), configHeader());
};

export default {
  listMember: listMember,
  getBalance: getBalance,
  getMemberInfo: getMemberInfo,
  createMember: createMember,
  editMember: editMember,
  deposit: deposit,
  WithdrawAll: WithdrawAll,
  deleteMember: deleteMember
};
