            mem: "",
            agent: "",
            isDownLine: false,
            // role: "3",
            // rolename: "User",

        };

        this.handleSelectChange = this.handleSelectChange.bind(this);
        this.handleToggleChange = this.handleToggleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setErrorMsg = this.setErrorMsg.bind(this);

        //this.onClick = this.onClick.bind(this);
        //this.onConfirmClick = this.onConfirmClick.bind(this)

        this.handleResetSubmit = this.handleResetSubmit.bind(this);
        //this.onSearchLevel = this.onSearchLevel.bind(this);
        this.onSearchAgentCode = this.onSearchAgentCode.bind(this);

        this.handleSelectMemberChange = this.handleSelectMemberChange.bind(this);
        //this.SearchAgent = this.SearchAgent.bind(this);
        this.handleSelectBrandChange = this.handleSelectBrandChange.bind(this);
    }

    handleSelectBrandChange(e) {
        e.preventDefault();
        console.log('e.target.value =>' + e.target.value);
        this.setState({ brand: e.target.value });
    }

    /* SearchAgent(agent_name) {
        console.log('get agent_code');
        const agents = this.state.data_agent.filter((agent) => {
            return agent.agent_name == agent_name;
        })

        const agent_code = agents.map((agent, index) => {
            return agent.agent_code;
        })
        //console.log('myrole:' + myrole);
        return agent_code[0];
    } */

    handleSelectMemberChange(e) {
        e.preventDefault();
        console.log('e.target.value =>' + e.target.value);
        this.setState({ mem: e.target.value, agent_code: this.onSearchAgentCode(e.target.value) });
        //const agent_code = this.SearchAgent(e.target.value);
        //Cookies.set('_Agent', e.target.value);
        //this.setState({ agent_code: agent_code });
    }



    /* onSearchLevel(level_name) {
        console.log('get myrole');
        const myrole = this.state.role_data.filter((role) => {
            return role.user_level_name == level_name;
        })

        const user_level_id = myrole.map((role, index) => {
            return role.user_level_id
        })
        return user_level_id[0];
    } */

    onSearchAgentCode(mem_username) {
        console.log('get Agent Code');
        const mems = this.state.data_member.filter((mem) => {
            return mem.mem_username == mem_username;
        })

        const agentCode = mems.map((mem, index) => {
            return mem.agent_code
        })
        //console.log('myrole:' + myrole);
        return agentCode;
    }

    handleSwitchChange = nr => () => {
        let switchNumber = `switch${nr}`;
        this.setState({
            [switchNumber]: !this.state[switchNumber]
        });
    }

    /* onClick() {
        this.dialog.showAlert('Hello Dialog!')
    } */

    handleResetSubmit() {
        Router.push("/wallets");
    }

    /* onConfirmClick() {
        this.dialog.show({
            title: 'Spiderbot confirm',
            body: 'Are you sure?',
            actions: [
                Dialog.CancelAction(() => {
                    dialog.hide();
                }),
                Dialog.OKAction(() => {

                })
            ],
            bsSize: 'small',
            onHide: (dialog) => {
                dialog.hide()
                console.log('closed by clicking background.')
            }
        })
    } */

    SearchAgent_LineUp(agent_code) {
        console.log('get agent_code');
        const agents = this.state.data_agent.filter((agent) => {
            return agent.agent_code == agent_code;
        })

        const agent_lineup = agents.map((agent, index) => {
            return agent.agent_lineup;
        })
        //console.log('myrole:' + myrole);
        return agent_lineup;
    }

    SearchAgent_LineUp2(data_agent, agent_code) {
        console.log('get agent_code');
        const agents = data_agent.filter((agent) => {
            return agent.agent_code == agent_code;
        })
        console.log('agents : ' + JSON.stringify(agents));
        const agent_lineup = agents.map((agent, index) => {
            return agent.agent_lineup;
        })
        //console.log('myrole:' + myrole);
        return agent_lineup;
    }



    async handleSubmit() {
        try {
            if (this.state.agent_code !== "" && this.state.user_name && this.state.user_pass) {
                if (this.state.user_pass === this.state.confirmpassword) {
                    //const urole = this.onSearchLevel(this.state.rolename);
