                        user_email: this.state.user_email, user_pass: this.state.user_pass, user_level: this.state.user_level,
                        user_status: user_status
                    };
                    const res = await ServiceUserAgent.createUserAgent(a);
                    console.log('res : ' + res.data);
                    if (res.data.status === 2000) {
                        console.log('Add User Agent Success!!!');
                        this.dialog.show({
                            title: 'Slot Admin information',
                            body: 'Add a user agent : ' + a.user_name + ' to system is successfully.',
                            actions: [
