import axios from "axios";
import { configHeader } from "../components/functions/configHeader";

var env = require('../env');

var endpoint = env.endpoint;

var listService = function listService() {
  return axios.get("".concat(endpoint, "/services"), configHeader());
};

var getService = function getService(id) {
  return axios.get("".concat(endpoint, "/services/").concat(id), configHeader());
};

var listServiceFromAgent = function listServiceFromAgent(AgentCode) {
  return axios.get("".concat(endpoint, "/services_agent/").concat(AgentCode), configHeader());
};

var createService = function createService(data) {
  //console.log('data : ' + data.password);
  return axios.post("".concat(endpoint, "/services"), data, configHeader());
};

var editService = function editService(data, id) {
  //console.log('data : ' + data);
  return axios.put("".concat(endpoint, "/services/").concat(id), data, configHeader());
};

var deleteService = function deleteService(id) {
  return axios["delete"]("".concat(endpoint, "/services/").concat(id), configHeader());
};

export default {
  listService: listService,
  getService: getService,
  listServiceFromAgent: listServiceFromAgent,
  createService: createService,
  editService: editService,
  deleteService: deleteService
};
